def update_rankings_white_values(apps, schema_editor):
    """Update ranking screens to use white values instead of yellow."""
    ScreenDesign = apps.get_model('digital_signage', 'ScreenDesign')

    # Update all ranking screens with new CSS
    ranking_slugs = [
        'jumpca-today-rankings',
        'jumpca-wtd-rankings',
        'jumpca-mtd-rankings',
    ]

    for slug in ranking_slugs:
        try:
            screen = ScreenDesign.objects.get(slug=slug)
            screen.css_code = JUMPCA_CSS_WHITE_VALUES
            screen.save()
        except ScreenDesign.DoesNotExist:
            pass


